	border: 1px solid #ccd0d5;
	text-align: right;
}
.wp-block-telex-block-tax-calculator .bd-tax-slab-table th:first-child,
.wp-block-telex-block-tax-calculator .bd-tax-slab-table td:first-child  {
	text-align: left;
}

